#用户表
DROP TABLE IF EXISTS t_user;
CREATE TABLE t_user (
	UserID int PRIMARY KEY AUTO_INCREMENT,
	UserName varchar(20) COMMENT '用户姓名 ',
    Password varchar(100) COMMENT '密码',
	BirthDate date,
    Age int GENERATED ALWAYS AS (2021-Year(BirthDate)),
	Gender enum('男 ', '女 '),
    Phone varchar(20),
    Email varchar(30),
    QQ varchar(20),
    WeChat varchar(20),
	Address varchar(100),
	City varchar(25),
	Zip varchar(10),
    RoleType int,
	Photo varchar(100) COMMENT '员工照片 ',
	Notes text COMMENT '员工简介 '
)AUTO_INCREMENT=1024;
